#include <stdint.h>

#ifndef NEW_ECC_PRIVATE_KEY_H_
#define NEW_ECC_PRIVATE_KEY_H_

/**********************************************************************************************************************
 Macro definitions
 *********************************************************************************************************************/
/** Encrypted key byte size **/
#define NEW_ECC_PRI_SIZE (48)

/**********************************************************************************************************************
 Global Typedef definitions
 *********************************************************************************************************************/
/** User key datas */
typedef struct NEW_ECC_PRI
{
    uint32_t    keytype;
    uint32_t    shared_key_number;
    uint8_t     initial_vector[16];
    uint8_t     encrypted_user_key[NEW_ECC_PRI_SIZE];
    uint8_t     crc[4];
} NEW_ECC_PRI_t;

/**********************************************************************************************************************
 External global variables
 *********************************************************************************************************************/
extern const NEW_ECC_PRI_t g_NEW_ECC_PRI;

/**********************************************************************************************************************
 Exported global functions
 *********************************************************************************************************************/

#endif /* NEW_ECC_PRIVATE_KEY_H_ */
